mapper.insert:
INSERT INTO ts_area (
) VALUES
( #{item.areaId, jdbcType=BIGINT}, #{item.areaCode, jdbcType=VARCHAR}, #{item.areaName, jdbcType=VARCHAR}, #{item.shortName, jdbcType=VARCHAR}, #{item.fullName, jdbcType=VARCHAR}, #{item.parentId, jdbcType=BIGINT}, #{item.path, jdbcType=VARCHAR}, #{item.level, jdbcType=INTEGER}, #{item.levelCode, jdbcType=VARCHAR}, #{item.sortNo, jdbcType=INTEGER}, #{item.comment, jdbcType=VARCHAR}, #{item.customFlag, jdbcType=INTEGER}, #{item.deleteFlag, jdbcType=INTEGER}, #{item.createOn, jdbcType=BIGINT}, #{item.createTime, jdbcType=BIGINT}, #{item.modifyOn, jdbcType=BIGINT}, #{item.modifyTime, jdbcType=BIGINT} )

mapper.select:
SELECT
,(SELECT COUNT(*) FROM ts_area WHERE parent_id = t.area_id AND delete_flag = 0) AS total FROM ts_area t
AND t.delete_flag = 0 AND t.area_code LIKE '${areaCode}%' AND t.area_name LIKE '%${areaName}%' AND t.parent_id = #{parentId,jdbcType=BIGINT} AND t.level = #{level,jdbcType=INTEGER}

SELECT
FROM ts_area
AND delete_flag = 0 AND (areaCode LIKE '%${condition.keywords}%' OR areaName LIKE '%${condition.keywords}%') AND area_code LIKE '${condition.areaCode}%' AND area_name LIKE '%${condition.areaName}%' AND parent_id = #{condition.parentId,jdbcType=BIGINT}
ORDER BY create_on DESC LIMIT #{pageIndex,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

mapper.update:
UPDATE ts_area
area_code = #{areaCode, jdbcType=VARCHAR}, area_name = #{areaName, jdbcType=VARCHAR}, short_name = #{shortName, jdbcType=VARCHAR}, full_name = #{fullName, jdbcType=VARCHAR}, parent_id = #{parentId, jdbcType=BIGINT}, path = #{path, jdbcType=VARCHAR}, level = #{level, jdbcType=INTEGER}, level_code = #{levelCode, jdbcType=VARCHAR}, sort_no = #{sortNo, jdbcType=INTEGER}, comment = #{comment, jdbcType=VARCHAR}, custom_flag = #{customFlag, jdbcType=INTEGER}, delete_flag = #{deleteFlag, jdbcType=INTEGER}, create_on = #{createOn, jdbcType=BIGINT}, create_time = #{createTime, jdbcType=BIGINT}, modify_on = #{modifyOn, jdbcType=BIGINT}, modify_time = #{modifyTime, jdbcType=BIGINT}
WHERE area_id = #{areaId,jdbcType=BIGINT}

UPDATE ts_area
area_code = #{area.areaCode, jdbcType=VARCHAR}, area_name = #{area.areaName, jdbcType=VARCHAR}, short_name = #{area.shortName, jdbcType=VARCHAR}, full_name = #{area.fullName, jdbcType=VARCHAR}, parent_id = #{area.parentId, jdbcType=BIGINT}, path = #{area.path, jdbcType=VARCHAR}, level = #{area.level, jdbcType=INTEGER}, level_code = #{area.levelCode, jdbcType=VARCHAR}, sort_no = #{area.sortNo, jdbcType=INTEGER}, comment = #{area.comment, jdbcType=VARCHAR}, custom_flag = #{area.customFlag, jdbcType=INTEGER}, delete_flag = #{area.deleteFlag, jdbcType=INTEGER}, create_on = #{area.createOn, jdbcType=BIGINT}, create_time = #{area.createTime, jdbcType=BIGINT}, modify_on = #{area.modifyOn, jdbcType=BIGINT}, modify_time = #{area.modifyTime, jdbcType=BIGINT}
AND delete_flag = 0 AND area_code LIKE '${condition.areaCode}%' AND area_name LIKE '%${condition.areaName}%' AND parent_id = #{condition.parentId,jdbcType=BIGINT}

UPDATE ts_area
SET path = #{path, jdbcType=VARCHAR}
WHERE area_id = #{areaId,jdbcType=BIGINT}
