mapper.select:
SELECT  

FROM ts_resource

     AND resource_name LIKE '%${resourceName}%'
     AND resource_no = #{resourceNo,jdbcType=VARCHAR}
     AND parent_id = #{parentId,jdbcType=BIGINT}

ORDER BY create_on DESC LIMIT 0,1

SELECT 

FROM ts_resource 

    AND delete_flag = 0
     AND (resource_name LIKE '%${condition.keywords}%' OR caption LIKE '%${condition.keywords}%') 
     AND resource_name LIKE '%${condition.resourceName}%'
     AND resource_no = #{condition.resourceNo,jdbcType=VARCHAR}
     AND parent_id = #{condition.parentId,jdbcType=BIGINT}

ORDER BY create_on DESC LIMIT #{pageIndex,jdbcType=INTEGER},#{pageSize,jdbcType=INTEGER}

