mapper.select:
SELECT

FROM (SELECT * FROM ts_account WHERE delete_flag = 0) t


        OR account_id = #{accountId,jdbcType=BIGINT}


        OR account = #{account,jdbcType=VARCHAR}


        OR mobile_number = #{mobileNumber,jdbcType=VARCHAR}


        OR email = #{email,jdbcType=VARCHAR}


        OR identity_card_no = #{identityCardNo,jdbcType=VARCHAR}


ORDER BY create_on DESC LIMIT 0,1

mapper.update:
UPDATE ts_account
account = #{account, jdbcType=VARCHAR}, name=#{name,jdbcType=VARCHAR}, mobile_number = #{mobileNumber, jdbcType=VARCHAR}, email = #{email, jdbcType=VARCHAR}, identity_card_no = #{identityCardNo, jdbcType=VARCHAR}, super_flag = #{superFlag, jdbcType=INTEGER}, admin_flag = #{adminFlag, jdbcType=INTEGER}, enable_flag = #{enableFlag, jdbcType=INTEGER}, delete_flag = #{deleteFlag, jdbcType=INTEGER}, next_change_flag = #{nextChangeFlag, jdbcType=INTEGER}, comment = #{comment, jdbcType=VARCHAR}, create_on = #{createOn, jdbcType=BIGINT}, create_time = #{createTime, jdbcType=BIGINT}, modify_on = #{modifyOn, jdbcType=BIGINT}, modify_time = #{modifyTime, jdbcType=BIGINT}, registration_id = #{registrationID, jdbcType=VARCHAR}
WHERE account_id = #{accountId,jdbcType=BIGINT}

mapper.select:
SELECT COUNT(*)
FROM ts_account


WHERE mobile_number = #{mobileNumber,jdbcType=VARCHAR}

    	SELECT

        FROM ts_account

        	1 = 1

	        	and account_id != #{accountId}

	        and ( 1 = 0

	        	or account = #{account}


	        	or mobile_number = #{mobileNumber}


	        	or email = #{email}


	        	or identity_card_no = #{identityCardNo}

	        )

        LIMIT 0,1
